int NMS_Plugin::enqueue(int batchSize, const void* const* inputs, void** outputs, void* workspace, cudaStream_t stream)
{
  //get workspace and set to 0
  CHECK(cudaMemset(workspace,0,getWorkspaceSize(batchSize)));
  size_t offset=0;
  void* dev_num_out=workspace+offset;
  offset+=batchSize*1*sizeof(int);
  void* dev_mask=workspace+offset;
  offset+=batchSize*box_num*sizeof(int);
  void* dev_index_buffer=workspace+offset;
  offset+=batchSize*keep_top_k*sizeof(int);
  
  if (mDataType == DataType::kFLOAT){
    nms_gpu(stream,
      reinterpret_cast<const float*>(inputs[0]),
      reinterpret_cast<float*>(outputs[0]),
      reinterpret_cast<float*>(outputs[1]),
      (int*)dev_num_out,
      (int*)dev_mask,
      (int*)dev_index_buffer,
      batchSize,
      box_num,
      box_dim,
      keep_top_k,
      nms_threshold,
      score_threshold);
  } else if (mDataType == DataType::kHALF){
    nms_gpu(stream,
      reinterpret_cast<const __half*>(inputs[0]),
      reinterpret_cast<__half*>(outputs[0]),
      reinterpret_cast<__half*>(outputs[1]),
      (int*)dev_num_out,
      (int*)dev_mask,
      (int*)dev_index_buffer,
      batchSize,
      box_num,
      box_dim,
      keep_top_k,
      dlr_float2half(nms_threshold),
      dlr_float2half(score_threshold));
  }
  return 0;
}

